mapper.insert:
insert into 
		member(
			  MEMIDX
			, MEMID
			, MEMPASSWORD
			, MEMNAME
			, MEMAGE
			, MEMGENDER
			, MEMEMAIL
			, MEMPROFILE
				)
		values(
			  member_idx.NEXTVAL
			, #{memID}
			, #{memPassword}
			, #{memName}
			, #{memAge}
			, #{memGender}
			, #{memEmail}
			, #{memProfile}
			  )
